import React from 'react';
import { Link } from 'react-router-dom';
import "./styles/Item.css";
import './styles/ItemList.css';

class Item extends React.Component {
  render() {

    return (

        <div className="Item">
        <div className="row">
            <div className="col-4 Item__Circulo">

                <div className="Item__Circulo--forma"> </div>
            </div>
            <div className="col-8 Item__Info">
                <p className="Item__Name"> {this.props.info} </p>
                <p className="Item__Creado">Creado en: </p>
            </div>

            <div className="col-12 Item__Muta"> Sin mutacion </div>
        </div>

      </div>
    );
  }
}

class BadgesList extends React.Component {
  render() {
    if (this.props.badges.length === 0) {
      return (
        <div>
          <h3>No badges were found</h3>
          <Link className="btn btn-primary" to="/badges/new">
            Create new badge
          </Link>
        </div>
      );
    }

    return (
      <div className="BadgesList">
        <ul className="list-unstyled">
          {this.props.badges.map(badge => {
            return (
              <li key={badge.id}>
                <BadgesListItem badge={badge} />
              </li>
            );
          })}
        </ul>
      </div>
    );
  }
}

export default BadgesList;
